mapper.select:
SELECT * FROM std_resource 
WHERE standard_status = #{standardStatus}
ORDER BY create_time DESC

SELECT * FROM std_resource 
WHERE standard_field = #{standardField}
ORDER BY create_time DESC

SELECT * FROM std_resource 
WHERE standard_classification = #{standardClassification}
ORDER BY create_time DESC

SELECT * FROM std_resource 
WHERE 1=1

    AND resource_code LIKE CONCAT('%', #{resource.resourceCode}, '%')


    AND chinese_name LIKE CONCAT('%', #{resource.chineseName}, '%')


    AND standard_status = #{resource.standardStatus}


    AND standard_field = #{resource.standardField}


    AND standard_classification = #{resource.standardClassification}

ORDER BY create_time DESC
